import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams(); // ID de la tarjeta
  const [tarjeta, setTarjeta] = useState(null);
  const [estudiantes, setEstudiantes] = useState([]);
  const [nombreAcudiente, setNombreAcudiente] = useState('');
  const [direccion, setDireccion] = useState('');
  const [estudiante, setEstudiante] = useState('');
  const [edad, setEdad] = useState('');
  const [celular, setCelular] = useState('');
  const [cedulaAcudiente, setCedulaAcudiente] = useState('');
  const [usuariosEstudiantes, setUsuariosEstudiantes] = useState([]);

  // Obtener detalles de la tarjeta
  useEffect(() => {
    const fetchTarjeta = async () => {
      const response = await fetch(`http://localhost:5000/tarjetas/${id}`);
      const data = await response.json();
      setTarjeta(data);
    };
    fetchTarjeta();
  }, [id]);

  // Obtener estudiantes de la tarjeta
  useEffect(() => {
    const fetchEstudiantes = async () => {
      const response = await fetch(`http://localhost:5000/tarjetas/${id}/estudiantes`);
      const data = await response.json();
      setEstudiantes(data);
    };
    fetchEstudiantes();
  }, [id]);

  // Obtener usuarios con rol "Estudiante"
  useEffect(() => {
    const fetchUsuariosEstudiantes = async () => {
      const response = await fetch('http://localhost:5000/usuarios/estudiantes');
      const data = await response.json();
      setUsuariosEstudiantes(data);
    };
    fetchUsuariosEstudiantes();
  }, []);

  // Crear nuevo estudiante
  const handleSubmit = async (e) => {
    e.preventDefault();
    const nuevoEstudiante = {
      nombre_acudiente: nombreAcudiente,
      direccion,
      estudiante,
      edad,
      celular,
      cedula_acudiente: cedulaAcudiente,
    };

    const response = await fetch(`http://localhost:5000/tarjetas/${id}/estudiantes`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(nuevoEstudiante),
    });

    if (response.ok) {
      const data = await response.json();
      alert('Estudiante creado exitosamente');
      setEstudiantes([...estudiantes, data]);
    }
  };

  return (
    <div>
      <h2>Detalles de la Tarjeta: {tarjeta && tarjeta.deporte}</h2>

      <h3>Estudiantes:</h3>
      <ul>
        {estudiantes.map(est => (
          <li key={est.estudiante_id}>{est.estudiante} - {est.nombre_acudiente}</li>
        ))}
      </ul>

      <h3>Añadir Estudiante</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre del Acudiente:
          <input
            type="text"
            value={nombreAcudiente}
            onChange={(e) => setNombreAcudiente(e.target.value)}
          />
        </label>
        <br />
        <label>
          Dirección:
          <input
            type="text"
            value={direccion}
            onChange={(e) => setDireccion(e.target.value)}
          />
        </label>
        <br />
        <label>
          Estudiante:
          <select value={estudiante} onChange={(e) => setEstudiante(e.target.value)}>
            <option value="">Selecciona un estudiante</option>
            {usuariosEstudiantes.map((user) => (
              <option key={user.user_id} value={user.user_id}>
                {user.username}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Edad:
          <select value={edad} onChange={(e) => setEdad(e.target.value)}>
            <option value="">Selecciona la categoría</option>
            <option value="8-10">8-10</option>
            <option value="9-12">9-12</option>
            <option value="13-16">13-16</option>
            <option value="17+">17+</option>
          </select>
        </label>
        <br />
        <label>
          Celular:
          <input
            type="number"
            value={celular}
            onChange={(e) => setCelular(e.target.value)}
          />
        </label>
        <br />
        <label>
          Cédula del Acudiente:
          <input
            type="number"
            value={cedulaAcudiente}
            onChange={(e) => setCedulaAcudiente(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Añadir Estudiante</button>
      </form>
    </div>
  );
};

export default TarjetaDetalles;
