// src/components/TarjetaDetalle.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TarjetaDetalle = () => {
  const { id } = useParams();  // Obtener el id de la URL
  const [tarjeta, setTarjeta] = useState(null);

  // Obtener los detalles de la tarjeta usando el ID
  useEffect(() => {
    axios.get(`http://localhost:3001/tarjetas/${id}`)
      .then(response => setTarjeta(response.data))
      .catch(error => console.error(error));
  }, [id]);

  if (!tarjeta) return <div>Cargando...</div>;

  return (
    <div style={detalleStyle}>
      <h1>{tarjeta.titulo}</h1>
      <p>{tarjeta.descripcion}</p>
    </div>
  );
};

// Estilo b치sico para la p치gina de detalle
const detalleStyle = {
  border: '1px solid #ccc',
  padding: '16px',
  margin: '16px',
  borderRadius: '8px',
  width: '50%',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
};

export default TarjetaDetalle;
