import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams();
  const [tarjeta, setTarjeta] = useState(null);
  const [estudiantes, setEstudiantes] = useState([]);
  const [formData, setFormData] = useState({
    nombre_acudiente: '',
    direccion: '',
    estudiante: '',
    categoria: '8-10',
    celular: '',
    cedula_acudiente: ''
  });

  // Obtener los detalles de la tarjeta
  useEffect(() => {
    axios.get(`http://localhost:5000/tarjetas/${id}`)
      .then(response => setTarjeta(response.data))
      .catch(error => console.error('Error al obtener detalles de la tarjeta:', error));
  }, [id]);

  // Obtener la lista de estudiantes
  useEffect(() => {
    axios.get('http://localhost:5000/estudiantes')
      .then(response => setEstudiantes(response.data))
      .catch(error => console.error('Error al obtener estudiantes:', error));
  }, []);

  // Manejar el cambio en los campos del formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Guardar estudiante
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/estudiantes', formData)
      .then(response => {
        alert('Estudiante creado correctamente');
        // Resetear el formulario despuÃ©s de guardar
        setFormData({
          nombre_acudiente: '',
          direccion: '',
          estudiante: '',
          categoria: '8-10',
          celular: '',
          cedula_acudiente: ''
        });
      })
      .catch(error => console.error('Error al guardar el estudiante:', error));
  };

  // Actualizar estudiante
  const handleUpdate = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:5000/estudiantes/${id}`, formData)
      .then(response => {
        alert('Estudiante actualizado correctamente');
      })
      .catch(error => console.error('Error al actualizar el estudiante:', error));
  };

  // Eliminar estudiante
  const handleDelete = () => {
    axios.delete(`http://localhost:5000/estudiantes/${id}`)
      .then(response => {
        alert('Estudiante eliminado correctamente');
      })
      .catch(error => console.error('Error al eliminar el estudiante:', error));
  };

  return (
    <div>
      <h1>Detalles de la Tarjeta</h1>
      {tarjeta && (
        <div>
          <h2>{tarjeta.deporte}</h2>
          <p>{tarjeta.descripcion}</p>
          <p>Entrenador: {tarjeta.entrenador}</p>
          <p>CategorÃ­a: {tarjeta.categoria}</p>
          <p>UbicaciÃ³n: {tarjeta.ubicacion}</p>
          <p>Horario: {tarjeta.horario}</p>
        </div>
      )}

      <h2>Formulario de Estudiante</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre del Acudiente:</label>
          <input
            type="text"
            name="nombre_acudiente"
            value={formData.nombre_acudiente}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>DirecciÃ³n:</label>
          <input
            type="text"
            name="direccion"
            value={formData.direccion}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Estudiante:</label>
          <select
            name="estudiante"
            value={formData.estudiante}
            onChange={handleInputChange}
            required
          >
            <option value="">Selecciona un estudiante</option>
            {estudiantes.map(est => (
              <option key={est.user_id} value={est.user_id}>
                {est.username}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>CategorÃ­a:</label>
          <select
            name="categoria"
            value={formData.categoria}
            onChange={handleInputChange}
            required
          >
            <option value="8-10">8-10</option>
            <option value="9-12">9-12</option>
            <option value="13-16">13-16</option>
            <option value="17+">17+</option>
          </select>
        </div>
        <div>
          <label>Celular:</label>
          <input
            type="text"
            name="celular"
            value={formData.celular}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>CÃ©dula del Acudiente:</label>
          <input
            type="text"
            name="cedula_acudiente"
            value={formData.cedula_acudiente}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Guardar</button>
        <button type="button" onClick={handleUpdate}>Actualizar</button>
        <button type="button" onClick={handleDelete}>Eliminar</button>
      </form>
    </div>
  );
};

export default TarjetaDetalles;
