import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams(); // Asegúrate de que el ID es correcto
  const [tarjeta, setTarjeta] = useState(null);
  const [estudiantes, setEstudiantes] = useState([]);

  useEffect(() => {
    // Obtener detalles de la tarjeta
    axios.get(`/tarjetas/${id}`)
      .then((response) => {
        setTarjeta(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener detalles de la tarjeta:', error.response ? error.response.data : error.message);
      });
  }, [id]);

  useEffect(() => {
    // Obtener estudiantes asociados a la tarjeta
    axios.get(`/estudiantes/${id}`)
      .then((response) => {
        setEstudiantes(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener estudiantes:', error.response ? error.response.data : error.message);
      });
  }, [id]);

  if (!tarjeta) {
    return <div>Cargando tarjeta...</div>;
  }

  return (
    <div>
      <h1>Detalles de la Tarjeta</h1>
      <p>Deporte: {tarjeta.deporte}</p>
      <p>Descripción: {tarjeta.descripcion}</p>
      <p>Entrenador: {tarjeta.entrenador}</p>
      <p>Categoría: {tarjeta.categoria}</p>
      <p>Ubicación: {tarjeta.ubicacion}</p>
      <p>Horario: {tarjeta.horario}</p>

      <h2>Estudiantes</h2>
      {estudiantes.length > 0 ? (
        <ul>
          {estudiantes.map((estudiante) => (
            <li key={estudiante.cedula_id}>
              Nombre del Acudiente: {estudiante.nombre_acudiente}<br/>
              Dirección: {estudiante.direccion}<br/>
              Categoría: {estudiante.edad}<br/>
              Celular: {estudiante.celular}<br/>
              Cédula del Acudiente: {estudiante.cedula_acudiente}
            </li>
          ))}
        </ul>
      ) : (
        <p>No hay estudiantes asociados a esta tarjeta.</p>
      )}
    </div>
  );
};

export default TarjetaDetalles;
