        const updatedTarjetas = await fetch('http://localhost:5000/tarjetas');
        const updatedData = await updatedTarjetas.json();
        setTarjetas(updatedData);
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage(editId ? 'Error al actualizar la tarjeta' : 'Error al crear la tarjeta');
    }
  };

  // Actualizar estudiante

  // Eliminar estudiante


  return (
    <div>
      <h1>Detalles de la Tarjeta</h1>
      {tarjeta && (
        <div>
          <h2>{tarjeta.deporte}</h2>
          <p>{tarjeta.descripcion}</p>
          <p>Entrenador: {tarjeta.entrenador}</p>
          <p>Categoría: {tarjeta.categoria}</p>
          <p>Ubicación: {tarjeta.ubicacion}</p>
          <p>Horario: {tarjeta.horario}</p>
        </div>
      )}


    </div>
  );
};

