import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams(); // ID de la tarjeta
  const [tarjeta, setTarjeta] = useState(null);
  const [estudiantes, setEstudiantes] = useState([]);
  const [nuevoEstudiante, setNuevoEstudiante] = useState({
    nombre_acudiente: '',
    direccion: '',
    estudiante_id: '',
    edad: '',
    celular: '',
    cedula_acudiente: '',
  });
  const [listaEstudiantesUsuarios, setListaEstudiantesUsuarios] = useState([]);

  // Obtener detalles de la tarjeta
  useEffect(() => {
    axios.get(`/tarjetas/${id}`)
      .then((response) => {
        setTarjeta(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener detalles de la tarjeta:', error);
      });
  }, [id]);

  // Obtener estudiantes asociados a la tarjeta
  useEffect(() => {
    axios.get(`/estudiantes/${id}`)
      .then((response) => {
        setEstudiantes(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener estudiantes:', error);
      });
  }, [id]);

  // Obtener lista de estudiantes (usuarios con rol "Estudiante")
  useEffect(() => {
    axios.get('/usuarios/estudiantes')
      .then((response) => {
        setListaEstudiantesUsuarios(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener lista de estudiantes:', error);
      });
  }, []);

  // Manejar cambios en el formulario
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNuevoEstudiante({ ...nuevoEstudiante, [name]: value });
  };

  // Enviar formulario para añadir estudiante
  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('/estudiantes', {
      ...nuevoEstudiante,
      tarjeta_id: id // Asociar el estudiante a esta tarjeta
    })
    .then((response) => {
      setEstudiantes([...estudiantes, response.data]);
      setNuevoEstudiante({
        nombre_acudiente: '',
        direccion: '',
        estudiante_id: '',
        edad: '',
        celular: '',
        cedula_acudiente: '',
      });
      console.log('Estudiante añadido correctamente');
    })
    .catch((error) => {
      console.error('Error al añadir estudiante:', error);
    });
  };

  return (
    <div>
      {tarjeta && (
        <div>
          <h2>Detalles de la Tarjeta</h2>
          <p>Deporte: {tarjeta.deporte}</p>
          <p>Descripción: {tarjeta.descripcion}</p>
          <p>Entrenador: {tarjeta.entrenador}</p>
          <p>Categoría: {tarjeta.categoria}</p>
          <p>Ubicación: {tarjeta.ubicacion}</p>
          <p>Horario: {tarjeta.horario}</p>
        </div>
      )}

      <h3>Estudiantes Asociados</h3>
      <ul>
        {estudiantes.map((estudiante) => (
          <li key={estudiante.cedula_id}>
            {estudiante.estudiante_id} - {estudiante.nombre_acudiente} - {estudiante.edad} años
          </li>
        ))}
      </ul>

      <h3>Añadir Estudiante</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre del Acudiente:</label>
          <input
            type="text"
            name="nombre_acudiente"
            value={nuevoEstudiante.nombre_acudiente}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Dirección:</label>
          <input
            type="text"
            name="direccion"
            value={nuevoEstudiante.direccion}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Estudiante:</label>
          <select
            name="estudiante_id"
            value={nuevoEstudiante.estudiante_id}
            onChange={handleInputChange}
            required
          >
            <option value="">Seleccione un estudiante</option>
            {listaEstudiantesUsuarios.map((usuario) => (
              <option key={usuario.user_id} value={usuario.user_id}>
                {usuario.username}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Edad:</label>
          <select
            name="edad"
            value={nuevoEstudiante.edad}
            onChange={handleInputChange}
            required
          >
            <option value="">Seleccione la categoría de edad</option>
            <option value="8-10">8-10</option>
            <option value="9-12">9-12</option>
            <option value="13-16">13-16</option>
            <option value="17+">17+</option>
          </select>
        </div>

        <div>
          <label>Celular:</label>
          <input
            type="text"
            name="celular"
            value={nuevoEstudiante.celular}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label>Cédula del Acudiente:</label>
          <input
            type="text"
            name="cedula_acudiente"
            value={nuevoEstudiante.cedula_acudiente}
            onChange={handleInputChange}
            required
          />
        </div>

        <button type="submit">Añadir Estudiante</button>
      </form>
    </div>
  );
};

export default TarjetaDetalles;
