import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TarjetaDetalles = () => {
  const { id } = useParams();
  const [tarjeta, setTarjeta] = useState(null);
  const [estudiantes, setEstudiantes] = useState([]); // Inicializa como un array vacío
  const [formData, setFormData] = useState({
    nombre_acudiente: '',
    direccion: '',
    estudiante_id: '',
    categoria: '',
    celular: '',
    cedula_acudiente: ''
  });
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    // Obtener detalles de la tarjeta
    axios.get(`http://localhost:5000/tarjetas/${id}`)
      .then(response => {
        setTarjeta(response.data);
      })
      .catch(error => {
        console.error('Error al obtener detalles de la tarjeta:', error);
      });

    // Obtener estudiantes asociados a la tarjeta
    axios.get(`http://localhost:5000/estudiantes/${id}`)
      .then(response => {
        setEstudiantes(response.data || []); // Asegúrate de que sea un array
      })
      .catch(error => {
        console.error('Error al obtener estudiantes:', error);
      });

    // Obtener lista de estudiantes del sistema
    axios.get('http://localhost:5000/usuarios/estudiantes')
      .then(response => {
        setUsuarios(response.data);
      })
      .catch(error => {
        console.error('Error al obtener estudiantes:', error);
      });
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('http://localhost:5000/estudiantes', {
      tarjeta_id: id,
      ...formData
    })
      .then(response => {
        console.log('Estudiante añadido:', response.data);
        // Actualizar la lista de estudiantes
        setEstudiantes([...estudiantes, response.data]);
      })
      .catch(error => {
        console.error('Error al añadir estudiante:', error);
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  return (
    <div>
      {tarjeta ? (
        <div>
          <h1>{tarjeta.deporte}</h1>
          <p>Descripción: {tarjeta.descripcion}</p>
          <p>Entrenador: {tarjeta.entrenador}</p>
          <p>Categoría: {tarjeta.categoria}</p>
          <p>Ubicación: {tarjeta.ubicacion}</p>
          <p>Horario: {tarjeta.horario}</p>

          <h2>Estudiantes</h2>
          {Array.isArray(estudiantes) && estudiantes.length > 0 ? (
            <ul>
              {estudiantes.map(estudiante => (
                <li key={estudiante.estudiante_id}>
                  {estudiante.nombre_acudiente} - {estudiante.estudiante} - {estudiante.categoria}
                </li>
              ))}
            </ul>
          ) : (
            <p>No hay estudiantes asociados a esta tarjeta.</p>
          )}

          <h2>Añadir Estudiante</h2>
          <form onSubmit={handleSubmit}>
            <label>
              Nombre Acudiente:
              <input type="text" name="nombre_acudiente" value={formData.nombre_acudiente} onChange={handleInputChange} />
            </label>
            <label>
              Dirección:
              <input type="text" name="direccion" value={formData.direccion} onChange={handleInputChange} />
            </label>
            <label>
              Estudiante:
              <select name="estudiante_id" value={formData.estudiante_id} onChange={handleInputChange}>
                <option value="">Selecciona un estudiante</option>
                {usuarios.map(usuario => (
                  <option key={usuario.user_id} value={usuario.user_id}>
                    {usuario.username}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Categoría:
              <select name="categoria" value={formData.categoria} onChange={handleInputChange}>
                <option value="">Selecciona una categoría</option>
                <option value="8-10">8-10</option>
                <option value="9-12">9-12</option>
                <option value="13-16">13-16</option>
                <option value="17+">17+</option>
              </select>
            </label>
            <label>
              Celular:
              <input type="number" name="celular" value={formData.celular} onChange={handleInputChange} />
            </label>
            <label>
              Cédula del Acudiente:
              <input type="number" name="cedula_acudiente" value={formData.cedula_acudiente} onChange={handleInputChange} />
            </label>
            <button type="submit">Añadir Estudiante</button>
          </form>
        </div>
      ) : (
        <p>Cargando detalles de la tarjeta...</p>
      )}
    </div>
  );
};

export default TarjetaDetalles;
