import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams(); // Obtener el ID de la tarjeta desde la URL
  const [tarjeta, setTarjeta] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Estados para el formulario
  const [deporte, setDeporte] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [entrenador, setEntrenador] = useState('');
  const [categoria, setCategoria] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [horario, setHorario] = useState('');

  // Fetch de la tarjeta, estudiantes y usuarios con rol "Estudiante"
  useEffect(() => {
    const fetchTarjeta = async () => {
      try {
        const response = await fetch(`http://localhost:5000/tarjetas/${id}`);
        const data = await response.json();

        if (response.ok) {
          setTarjeta(data);
          // Establecer los valores del formulario con los datos de la tarjeta
          setDeporte(data.deporte);
          setDescripcion(data.descripcion);
          setEntrenador(data.entrenador_id); // Aquí debes ajustar según los datos de tu API
          setCategoria(data.categoria);
          setUbicacion(data.ubicacion);
          setHorario(data.horario);
        } else {
          setError(data.message);
        }
      } catch (error) {
        setError('Error al obtener los detalles de la tarjeta');
      } finally {
        setLoading(false);
      }
    };
    fetchTarjeta();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedTarjeta = {
      deporte,
      descripcion,
      entrenador_id: entrenador, // Ajustar según tu backend
      categoria,
      ubicacion,
      horario
    };

    try {
      const response = await fetch(`http://localhost:5000/tarjetas/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTarjeta),
      });

      if (!response.ok) {
        const data = await response.json();
        setError(data.message);
      } else {
        alert('Tarjeta actualizada correctamente');
      }
    } catch (error) {
      setError('Error al actualizar la tarjeta');
    }
  };

  if (loading) {
    return <p>Cargando detalles de la tarjeta...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-logo">
          <img 
            src="https://scontent.fbog2-4.fna.fbcdn.net/v/t39.30808-6/457086553_10162305170409734_7645391511123315340_n.jpg?_nc_cat=101&ccb=1-7&_nc_sid=aa7b47&_nc_eui2=AeHLefgosoin-hQJ09IYGtb0xTw9IMDv-q7FPD0gwO_6runAq0OxQVp25yZYkPSLw0sNKQVPls99KdFFXqEeVW83&_nc_ohc=55mTPLeIil8Q7kNvgGdNaMw&_nc_ht=scontent.fbog2-4.fna&_nc_gid=Arp-NZNI9glYjRbZXX7MpSo&oh=00_AYAJ2zvAdZ34cc5Mqhz_P0_TVJdN65lMDdfl5AHwn3fj7g&oe=66ED8177" 
            alt="Logo" 
            className="logo" 
          />
          <h1 className="navbar-title">CLUB DEPORTIVO CANTERA SPORT</h1>
        </div>
        <ul className="navbar-links">
          <li><a href="/">Inicio</a></li>
          <li><a href="/about">Acerca de</a></li>
          <li><a href="/contact">Contacto</a></li>
        </ul>
      </nav>

      <h1>{tarjeta.deporte}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Deporte:
          <input 
            type="text" 
            value={deporte} 
            onChange={(e) => setDeporte(e.target.value)} 
          />
        </label>
        <br />

        <label>
          Descripción:
          <input 
            type="text" 
            value={descripcion} 
            onChange={(e) => setDescripcion(e.target.value)} 
          />
        </label>
        <br />

        <label>
          Entrenador ID:
          <input 
            type="number" 
            value={entrenador} 
            onChange={(e) => setEntrenador(e.target.value)} 
          />
        </label>
        <br />

        <label>
          Categoría:
          <select value={categoria} onChange={(e) => setCategoria(e.target.value)}>
            <option value="8-10">8-10</option>
            <option value="9-12">9-12</option>
            <option value="13-16">13-16</option>
            <option value="17+">17+</option>
          </select>
        </label>
        <br />

        <label>
          Ubicación:
          <input 
            type="text" 
            value={ubicacion} 
            onChange={(e) => setUbicacion(e.target.value)} 
          />
        </label>
        <br />

        <label>
          Horario:
          <select value={horario} onChange={(e) => setHorario(e.target.value)}>
            <option value="8-12">8-12</option>
            <option value="3-6">3-6</option>
          </select>
        </label>
        <br />

        <button type="submit">Actualizar Tarjeta</button>
      </form>
    </div>
  );
};

export default TarjetaDetalles;
