import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const TarjetaDetalles = () => {
  const { id } = useParams();
  const [tarjeta, setTarjeta] = useState(null);
  const [nombre_acudiente, setNombreAcudiente] = useState('');
  const [direccion, setDireccion] = useState('');
  const [students, setStudents] = useState([]);
  const [estudianteId, setEstudianteId] = useState('');
  const [edad, setEdad] = useState('8-10');
  const [celular, setCelular] = useState('');
  const [cedula_acudiente, setCedulaAcudiente] = useState('');
  const [estudiantes, setEstudiantes] = useState([]);
  const [editId, setEditId] = useState(null);
  const [message, setMessage] = useState('');


  // Obtener los detalles de la tarjeta
  useEffect(() => {
    axios.get(`http://localhost:5000/tarjetas/${id}`)
      .then(response => setTarjeta(response.data))
      .catch(error => console.error('Error al obtener detalles de la tarjeta:', error));
  }, [id]);

  // Obtener la lista de estudiantes
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('http://localhost:5000/students');
        const data = await response.json();
        setStudents(data);
        setEstudianteId(data[0]?.user_id || '');
      } catch (error) {
        console.error('Error al estudiantes:', error);
      }
    };

    const fetchEstudiantes = async () => {
      try {
        const response = await fetch('http://localhost:5000/estudiantes');
        const data = await response.json();
        setEstudiantes(data);
      } catch (error) {
        console.error('Error al obtener estudiantes:', error);
      }
    };

    fetchStudents();
    fetchEstudiantes();
  }, []);





  // Guardar estudiante
  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editId ? 'PUT' : 'POST';
    const url = editId ? `http://localhost:5000/estudiantes/${editId}` : 'http://localhost:5000/estudiantes';

    try {
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nombre_acudiente,
          direccion,
          estudiante: estudianteId,
          edad,
          celular,
          cedula_acudiente,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(editId ? 'Estudiante actualizado correctamente' : 'Estudiante creado correctamente');
        setNombreAcudiente('');
        setDireccion('');
        setCelular('');
        setCedulaAcudiente('');
        setEdad('8-10');
        setEstudianteId(students[0]?.user_id || ''); 
        setEditId(null);

        const updatedTarjetas = await fetch('http://localhost:5000/estudiantes');
        const updatedData = await updatedTarjetas.json();
        setEstudiantes(updatedData);
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage(editId ? 'Error al actualizar el estudiante' : 'Error al crear el estudiante');
    }
  };

  // Actualizar estudiante


  const handleEdit = (estudiante) => {
    setEditId(estudiante.cedula_id);
    setDeporte(estudiante.nombre_acudiente);
    setDescripcion(estudiante.direccion);
    setEntrenadorId(estudiante.estudiante);
    setCategoria(estudiante.edad);
    setUbicacion(estudiante.celular);
    setHorario(estudiante.cedula_acudiente);
  };

  // Eliminar estudiante

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/estudiantes/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setMessage('Estudiante eliminada correctamente');
        setEstudiantes(estudiantes.filter((estudiante) => estudiante.cedula_id !== id));
      } else {
        setMessage('Error al eliminar el estudiante');
      }
    } catch (error) {
      setMessage('Error al eliminar la tarjeta');
    }
  };


  return (
    <div>
      <h1>Detalles de la Tarjeta</h1>
      {tarjeta && (
        <div>
          <h2>{tarjeta.deporte}</h2>
          <p>{tarjeta.descripcion}</p>
          <p>Entrenador: {tarjeta.entrenador}</p>
          <p>Categoría: {tarjeta.categoria}</p>
          <p>Ubicación: {tarjeta.ubicacion}</p>
          <p>Horario: {tarjeta.horario}</p>
        </div>
      )}
    <div className='container'>
      <h1>{editId ? 'Actualizar Estudiante' : 'Crear Estudiante'}</h1>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Nombre acudiente'
          value={nombre_acudiente}
          onChange={(e) => setNombreAcudiente(e.target.value)}
          required
        />
        <input
          type='text'
          placeholder='direccion'
          value={direccion}
          onChange={(e) => setDireccion(e.target.value)}
          required
        />
        <select value={estudianteId} onChange={(e) => setEstudianteId(e.target.value)} required>
          {students.map((estudiante) => (
            <option key={estudiante.user_id} value={estudiante.user_id}>
              {estudiante.username}
            </option>
          ))}
        </select>
        <select value={edad} onChange={(e) => setEdad(e.target.value)} required>
        <option value="8-10">8-10</option>
            <option value="9-12">9-12</option>
            <option value="13-16">13-16</option>
            <option value="17+">17+</option>
        </select>
        <input
          type='text'
          placeholder='celular'
          value={celular}
          onChange={(e) => setCelular(e.target.value)}
          required
        />
        <input
          type='text'
          placeholder='cedula acudiente'
          value={cedula_acudiente}
          onChange={(e) => setCedulaAcudiente(e.target.value)}
          required
        />
        <button type="submit">{editId ? 'Actualizar' : 'Crear'}</button>
      </form>
      {message && <p>{message}</p>}

      <h2>Estudiantes existentes</h2>
      <ul>
      {estudiantes.map((estudiante) => (
            <li key={estudiante.cedula_id_id}>
              <Link to={`/estudiantes/${estudiante.cedula_id}`}>
                <strong>{estudiante.nombre_acudiente}</strong> - {estudiante.direccion} (Entrenador: {estudiante.estudiante})
              </Link>
              <div>
                <button onClick={() => handleEdit(estudiante)}>Editar</button>
                <button onClick={() => handleDelete(estudiante.cedula_id)}>Eliminar</button>
              </div>
            </li>
          ))}
      </ul>        
    </div>

    </div>
  );
};

export default TarjetaDetalles;
